package com.yyjcccc.hessianser.usage;

import java.io.Serializable;

public class Person implements Serializable {
	public String name;
	public int age;

	public int getAge() {
		return age;
	}

	public String getName() {
		return name;
	}

	public void setAge(int age) {
		this.age = age;
